import './App.css';
import { useState } from 'react';
import Password from './passowrd';


function App() {

  const [delta,setDelta] = useState(0)
  const [passVal,setPass] = useState('weak')
  const [passVibe, setPassVibe] = useState(false)


  /*
  function chnageSubmitMode(data){
    setVibe(data)
  }
*/


  function ChangeNumber(event){
    console.log(event);
    setDelta(Number(event.target.value))
    const value = this.target.value;
    if(value.length > 100 )
   {
     // set the state of isEnable to be true to make the button to be enable
     setPassVibe({isEnable : true})
   }


  }


  function setPassowrd(pass){
    setPass(pass)
  }



  return (
    <div className="App">
      <input type = "number" value={delta} onChange = {ChangeNumber}/>
      <button disabled={passVibe}  >submit</button>
      <p></p>
        your password is {passVal}
      <Password  delta = {delta} setPassowrd = {setPassowrd} ></Password>
    </div>
  );
}

export default App;
